import React from 'react';
import GoogleMapReact from 'google-map-react';
import { useState } from 'react';
import { useEffect } from 'react';
import api from '../../api';
import Modal from 'react-modal';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';



const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)'
  }
};

const CustomMarker = ({ text, onClick }) => (
  <div style={{
    color: 'white',
    background: 'grey',
    padding: '15px 10px',
    display: 'inline-flex',
    textAlign: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: '100%',
    transform: 'translate(-50%, -50%)'
  }} onClick={onClick}>
    {text}
  </div>
);

const ModalMaker = ({parking}) => (
  <Modal
    isOpen={isDialogOpen}
    onRequestClose={closeDialog}
    style={customStyles}
  >
    <h2>{parking.pname}</h2>
    <p>This is the dialog content</p>
    <button onClick={closeDialog}>Close</button>
  </Modal>
)




const Map = () => {
  const [parkings, setParkings] = useState(null)
  const [isDialogOpen, setIsDialogOpen] = React.useState(false);

  const openDialog = () => {
    setIsDialogOpen(true);
  };

  const closeDialog = () => {
    setIsDialogOpen(false);
  };


  function onLoad(){
    api.get("/getParkings", {withCredentials: true})
    .then(res=>{
      setParkings(res.data);
      console.log(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }
  
  useEffect(() => {
    onLoad();
  }, [])

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyDRA8KWu6hfAC7vyYG8h4FQ48UzNymOjUc' }}
        defaultCenter={{ lat: 18.622761, lng:  73.741953 }}
        defaultZoom={13}
      >
        {parkings?.map((parking)=>{
          return (
          <>
            <CustomMarker lat={parking.plat} lng={parking.plng} text={parking.pname} 
              onClick={openDialog} />
            <ModalMaker parking={parking} />
            </>
            );
        })}
        
      </GoogleMapReact>
      {/* {
        parkings?.map((parking)=>{
          return (
            <Modal
              isOpen={isDialogOpen}
              onRequestClose={closeDialog}
              style={customStyles}
            >
              <h2>{parking.pname}</h2>
              <p>This is the dialog content</p>
              <button onClick={closeDialog}>Close</button>
            </Modal>
          )
        })
      } */}
    </div>
  );
};

export default Map;
