import React from 'react';
import GoogleMapReact from 'google-map-react';

const CustomMarker = ({ text, onClick }) => (
  <div style={{
    color: 'white',
    background: 'grey',
    padding: '15px 10px',
    display: 'inline-flex',
    textAlign: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: '100%',
    transform: 'translate(-50%, -50%)'
  }} onClick={onClick}>
    {text}
  </div>
);

const Map = () => {
  const locations = [
    { lat: 40.756795, lng: -73.954298, name: 'Location 1' },
    { lat: 40.756795, lng: -73.954299, name: 'Location 2' },
    { lat: 40.756796, lng: -73.954298, name: 'Location 3' }
  ];

  const [isPopupOpen, setIsPopupOpen] = React.useState(false);
  const [selectedLocation, setSelectedLocation] = React.useState(null);

  const openPopup = location => {
    setSelectedLocation(location);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyDRA8KWu6hfAC7vyYG8h4FQ48UzNymOjUc' }}
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      >
        {parkings?.map(location => (
          <CustomMarker lat={location.lat} lng={location.lng} text={location.name} onClick={() => openPopup(location)} />
        ))}
      </GoogleMapReact>
      {isPopupOpen && selectedLocation && (
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
          <p>Selected location: {selectedLocation.name}</p>
          <button onClick={closePopup}>Close</button>
        </div>
      )}
    </div>
  );
};

export default Map;
