import React from 'react';
import GoogleMapReact from 'google-map-react';
import { useEffect } from 'react';
import api from '../../api';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CustomMarker = ({ text, onClick }) => (
  <div style={{
    color: 'white',
    background: 'grey',
    padding: '15px 10px',
    display: 'inline-flex',
    textAlign: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: '100%',
    transform: 'translate(-50%, -50%)'
  }} onClick={onClick}>
    {text}
  </div>
);

const Map = () => {
  const [parkings, setParkings] = useState(null);
  const navigate = useNavigate();

  function onLoad(){
    api.get("/getParkings", {withCredentials: true})
    .then(res=>{
      setParkings(res.data);
      console.log(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }
  
  useEffect(() => {
    onLoad();
  }, [])

  const [isPopupOpen, setIsPopupOpen] = React.useState(false);
  const [selectedLocation, setSelectedLocation] = React.useState(null);

  const openPopup = location => {
    setSelectedLocation(location);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };
  const onSelected = () => {
    closePopup()
    navigate("/parking", {state: { parkingId: selectedLocation._id}})
  }

  function getPosition() {
    if(navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        console.log(position.coords)
        console.log(`Your current position is:`);
        console.log(`Latitude : ${latitude}`);
        console.log(`Longitude: ${longitude}`);
      });
    } 
    else
      alert("Not Supported");
  }

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyDRA8KWu6hfAC7vyYG8h4FQ48UzNymOjUc' }}
        defaultCenter={{ lat: latitude, lng: longitude }}
        defaultZoom={13}
      >
        {parkings?.map(location => (
          <CustomMarker lat={location.plat} lng={location.plng} text={location.pname} onClick={() => openPopup(location)} />
        ))}
      </GoogleMapReact>
      {isPopupOpen && selectedLocation && (
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', background: 'white' }}>
          <p>Selected location: {selectedLocation.pname}</p>
          <p>{selectedLocation._id}</p>
          <button onClick={closePopup}>Close</button>
          <button onClick={onSelected}>Select</button>
        </div>
      )}
    </div>
  );
};

export default Map;
