import React from 'react';
import GoogleMapReact from 'google-map-react';
import { useEffect } from 'react';
import api from '../../api';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CustomMarker = ({ text, onClick }) => (
  <div style={{
    color: 'white',
    background: 'grey',
    padding: '15px 10px',
    display: 'inline-flex',
    textAlign: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: '100%',
    transform: 'translate(-50%, -50%)'
  }} onClick={onClick}>
    {text}
  </div>
);

const Map = () => {
  const [parkings, setParkings] = useState(null);
  const [lati, setlati] = useState(0);
  const [longi, setlongi] = useState(0);

  const navigate = useNavigate();

  function onLoad(){
    api.get("/getParkings", {withCredentials: true})
    .then(res=>{
      setParkings(res.data);
      console.log(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }
  
  useEffect(() => {
    onLoad();
    getPosition();
  }, [])

  const [isPopupOpen, setIsPopupOpen] = React.useState(false);
  const [selectedLocation, setSelectedLocation] = React.useState(null);

  const openPopup = location => {
    setSelectedLocation(location);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };
  const onSelected = () => {
    closePopup()
    navigate("/parking", {state: { parkingId: selectedLocation._id}})
  }

  const center = {}

  function getPosition() {
    if(navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          console.log(position.coords.latitude)
          console.log(position.coords.longitude)
          setlati(position.coords.latitude)
          setlongi(position.coords.longitude)
          
          console.log(lati)
          console.log(longi)
        },
        (error) => {
          console.log(error)
        },
      );
    } 
    else
      alert("Not Supported");
  }

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      {(lati && longi)?
      <>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyDRA8KWu6hfAC7vyYG8h4FQ48UzNymOjUc' }}
        defaultCenter={{ lat: lati, lng:  longi }}
        defaultZoom={13}
      >
        {console.log(center.lat)}
        {parkings?.map(location => (
          <CustomMarker lat={location.plat} lng={location.plng} text={location.pname} onClick={() => openPopup(location)} />
        ))}
      </GoogleMapReact>
      {isPopupOpen && selectedLocation && (
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', background: 'white' }}>
          <p>Selected location: {selectedLocation.pname}</p>
          <p>{selectedLocation._id}</p>
          <button onClick={closePopup}>Close</button>
          <button onClick={onSelected}>Select</button>
        </div>
      )}
      </>:<>Loading</>}
    </div>
  );
};

export default Map;
