import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Alert from '@mui/material/Alert';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';

import api from '../../api'


export default function Login() {


  const [alert, setAlert] = useState(0);
  const theme = createTheme();

  const messages = {
    400: ['error', "Required * fields can't be empty"],
    401: ['error', "OTP is invalid"],
    200: ['success', "Log In successful, redirecting to home..."],
    201: ['info', "OTP sent successfully!!"],
    404: ['error', "User doesn't exist!!"],
    500: ['error', "Internal Server error!"]
  }

  const closeAlert = () => {
    onAlertClosed(0);
  }

  function handleSubmit(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    const otpData = data.get("otp");
    console.log(otpData);
    console.log(location.state.userId)
    api.post("/verifyUser", {
      userId: location.state.userId,
      otp: otpData,
    })
    .then(function(res){
      setAlert(res.status)
      setTimeout(function(){
        console.log(res);
        navigate("/login");
      }, 3000)
    })
    .catch(function(err){
      console.log(err);
      setAlert(err.response.status);
    })
  }

  return (
    <>
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 3,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
          >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Register
          </Typography>
          <br></br>
          <Box>
            {(alert)? <><Alert severity={messages[alert][0]} variant='filled' onClose={onAlertClosed}> {messages[alert][1]} </Alert>
          <br></br></>:<></>}
          </Box>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Log In
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="/register" variant="body2">
                  Not registered yet? Sign up
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
    </>
  )
}
